import React, { useState, useEffect, useRef } from 'react';
import ROSLIB from 'roslib';
import { Alert } from 'react-bootstrap';

const TemperatureDisplay = () => {
  const [temperatures, setTemperatures] = useState({});
  const rosRef = useRef(null);
  const staleThreshold = 5000; // 5 seconds threshold for considering data as stale

  useEffect(() => {
    const connectToROS = () => {
      if (rosRef.current) {
        rosRef.current.close();
      }
  
      const ros = new ROSLIB.Ros({
        url: 'ws://192.168.0.193:9090'
      });
  
      ros.on('connection', () => {
        console.log('Connected to websocket server.');
        rosRef.current = ros;
        subscribeToTopics();
      });
  
      ros.on('error', (error) => {
        console.log('Error connecting to websocket server: ', error);
        setTimeout(connectToROS, 5000); // Attempt to reconnect every 5 seconds
      });
  
      ros.on('close', () => {
        console.log('Connection to websocket server closed.');
        setTimeout(connectToROS, 5000); // Attempt to reconnect every 5 seconds
      });
    };
  
    const subscribeToTopics = () => {
      const topics = ['/temperature_1', '/temperature_2']; // Add more as needed
      topics.forEach(topic => {
        const subscriber = new ROSLIB.Topic({
          ros: rosRef.current,
          name: topic,
          messageType: 'std_msgs/Float32'
        });
  
        subscriber.subscribe((message) => {
          setTemperatures(prevTemps => ({
            ...prevTemps,
            [topic]: {
              value: message.data,
              lastUpdated: Date.now()
            }
          }));
        });
      });
    };
  
    connectToROS();
  
    const checkStaleData = setInterval(() => {
      const currentTime = Date.now();
      setTemperatures(prevTemps => {
        const newTemps = { ...prevTemps };
        Object.keys(newTemps).forEach(topic => {
          if (currentTime - newTemps[topic].lastUpdated > staleThreshold) {
            newTemps[topic] = { ...newTemps[topic], value: 'None' };
          }
        });
        return newTemps;
      });
    }, 1000);
  
    return () => {
      clearInterval(checkStaleData);
      if (rosRef.current) {
        rosRef.current.close();
      }
    };
  }, []);

  return (
    <div className="row">
      <div className="col-md-4 mt-5 mx-5"> {/* Adjust the column size as needed */}
        <h2 className="text-center mb-4">Temperature Sensors</h2>
        <ul className="list-unstyled">
          {Object.entries(temperatures).map(([topic, { value }]) => {
            let alertVariant = "light";
            if (value !== 'None') {
              if (value > 80) {
                alertVariant = "danger"; 
              } else if (value < 20) {
                alertVariant = "info"; 
              }
            }

            return (
              <li key={topic}>
                <Alert variant={alertVariant} className="d-flex justify-content-between align-items-center">
                  <strong>{topic}:</strong> {value === 'None' ? value : `${value.toFixed(2)} °C`}
                </Alert>
              </li>
            );
          })}
        </ul>
      </div>
    </div>
  );
};

export default TemperatureDisplay;
