import React, { useEffect, useRef } from 'react';
import ROSLIB from 'roslib';

const CameraFeed = () => {
    const videoStreamUrl = 'http://192.168.0.191:8080/stream?topic=/usb_cam/image_raw';

    useEffect(() => {
        const ros = new ROSLIB.Ros({
            url: 'ws://192.168.0.191:9090'
        });

        const connectToROS = () => {
            ros.on('connection', () => {
                console.log('Connected to websocket server.');
            });

            ros.on('error', (error) => {
                console.log('Error connecting to websocket server: ', error);
                setTimeout(connectToROS, 5000); // Attempt to reconnect every 5 seconds
            });

            ros.on('close', () => {
                console.log('Connection to websocket server closed.');
                setTimeout(connectToROS, 5000); // Attempt to reconnect every 5 seconds
            });
        };

        connectToROS();

        return () => {
            ros.close(); 
        };
    }, []);


    return (
        <div className="col-6 col-md-3 mt-4 mx-4">
        <div className="camera-feed-container d-flex justify-content-center position-relative" style={{maxWidth: '640px'}}>
          {/* If Bootstrap version supports aspect ratio classes, use those instead of custom padding-top */}
          <img src={videoStreamUrl} alt="Live Video Feed" className="camera-feed-image position-absolute top-0 start-0 w-100 h-100" style={{objectFit: 'cover'}} />
        </div>
        </div>
      );
};

export default CameraFeed;
