import React, { useEffect, useRef } from 'react';
import ROSLIB from 'roslib';


const CameraFeed = () => {
    const canvasRef = useRef(null);
    
    useEffect(() => {
        const ros = new ROSLIB.Ros({
            url: 'ws://192.168.0.191:9090'
        });

        const connectToROS = () => {
            ros.on('connection', () => {
                console.log('Connected to websocket server.');
                subscribeToCamera();
            });

            ros.on('error', (error) => {
                console.log('Error connecting to websocket server: ', error);
                setTimeout(connectToROS, 5000); // Attempt to reconnect every 5 seconds
            });

            ros.on('close', () => {
                console.log('Connection to websocket server closed.');
                setTimeout(connectToROS, 5000); // Attempt to reconnect every 5 seconds
            });
        };

        const subscribeToCamera = () => {
            new MJPEGCANVAS.Viewer({
                divID: 'mjpeg_canvas',
                host: '192.168.0.193',
                port: 8080,
                width: 640,
                height: 480,
                topic: '/usb_cam/image_raw'
            });
        };

        connectToROS();

        return () => {
            ros.close(); 
        };
    }, []);


    return (
        <div>
          <h2>Camera Feed</h2>
          <div id="mjpeg_canvas" ref={canvasRef} style={{ width: '640px', height: '480px' }}></div>
        </div>
      );
};

export default CameraFeed;
