import React, { useState, useEffect } from 'react';
import ROSLIB from 'roslib';
import TemperatureDisplay from '../components/TemperatureDisplay';
import MotorControl from '../components/MotorControl';

function HomePage() {
  const [connected, setConnected] = useState(false);

  useEffect(() => {
    const connectToROS = () => {
      const ros = new ROSLIB.Ros({
        url: 'ws://192.168.0.191:9090'
      });

      ros.on('connection', () => {
        console.log('Connected to websocket server.');
        setConnected(true);
      });

      ros.on('error', (error) => {
        console.log('Error connecting to websocket server: ', error);
        setConnected(false);
        setTimeout(() => connectToROS(), 5000); // Attempt to reconnect every 5 seconds
      });

      ros.on('close', () => {
        console.log('Connection to websocket server closed.');
        setConnected(false);
        setTimeout(() => connectToROS(), 5000); // Attempt to reconnect every 5 seconds
      });

      return ros;
    };

    const ros = connectToROS();

    return () => {
      ros.close();
    };
  }, []);

  return (
    <div className="App">
      <div className='mt-3'>
        <h1 className='text-center'>TBM Monitoring Dashboard</h1>
        <div className="status-indicator">
          <h2 className={`text-center ${connected ? 'text-success' : 'text-danger'}`}>
            {connected ? 'Connected to ROSBridge' : 'Disconnected from ROSBridge'}
          </h2>
        </div>
        <div className="row">
          <TemperatureDisplay />
          <MotorControl />
        </div>
      </div>
    </div>
  );
}

export default HomePage;
