import React, { useState, useEffect } from 'react';
import ROSLIB from 'roslib';

const TemperatureDisplay = () => {
  const [temperatures, setTemperatures] = useState({});
  const [lastUpdated, setLastUpdated] = useState({});
  const [connected, setConnected] = useState(false);
  const rosRef = React.useRef(null); // Ref to store the ROS instance
  const staleThreshold = 5000; // 5 seconds threshold for considering data as stale

  useEffect(() => {
    const connectToROS = () => {
      const ros = new ROSLIB.Ros({
        url: 'ws://192.168.0.193:9090'
      });

      ros.on('connection', () => {
        console.log('Connected to websocket server.');
        setConnected(true);
        subscribeToTopics(ros);
      });

      ros.on('error', (error) => {
        console.log('Error connecting to websocket server: ', error);
        setConnected(false);
        cleanup();
        setTimeout(() => connectToROS(), 5000); // Attempt to reconnect every 5 seconds
      });

      ros.on('close', () => {
        console.log('Connection to websocket server closed.');
        setConnected(false);
        cleanup();
        setTimeout(() => connectToROS(), 5000); // Attempt to reconnect every 5 seconds
      });

      rosRef.current = ros; // Store the ROS instance in the ref
    };

    const subscribeToTopics = (ros) => {
      // Define topic names for temperature sensors
      const topics = ['/temperature_1', '/temperature_2'];  // Add more as needed

      // Create subscribers for each topic
      const subscribers = topics.map(topic => {
        const subscriber = new ROSLIB.Topic({
          ros: ros,
          name: topic,
          messageType: 'std_msgs/Float32'
        });

        subscriber.subscribe((message) => {
          // Handle incoming message
          setTemperatures(prevTemps => ({
            ...prevTemps,
            [topic]: message.data
          }));
          setLastUpdated(prevTimes => ({
            ...prevTimes,
            [topic]: Date.now()
          }));
        });

        return subscriber;
      });

      // Store the subscribers in the ref to clean up later
      rosRef.current.subscribers = subscribers;
    };

    const cleanup = () => {
      if (rosRef.current) {
        if (rosRef.current.subscribers) {
          rosRef.current.subscribers.forEach(subscriber => subscriber.unsubscribe());
        }
        rosRef.current.close();
      }
    };

    connectToROS();

    const intervalId = setInterval(() => {
      setLastUpdated(prevTimes => {
        const currentTime = Date.now();
        const newTemps = { ...temperatures };
        Object.keys(prevTimes).forEach(topic => {
          if (currentTime - prevTimes[topic] > staleThreshold) {
            newTemps[topic] = 'None';
          }
        });
        setTemperatures(newTemps);
        return prevTimes;
      });
    }, 1000);

    return () => {
      cleanup();
      clearInterval(intervalId);
    };
  }, [temperatures]); // Dependency array to re-run useEffect when temperatures changes

  return (
    <div className="temperature-display">
      <h2 className="text-center">Temperature Sensors</h2>
      <ul>
        {Object.entries(temperatures).map(([topic, temp]) => (
          <li key={topic}>
            <strong>{topic}:</strong> {temp === 'None' ? temp : `${temp.toFixed(2)} °C`}
          </li>
        ))}
      </ul>
      <div className="connection-status">
        {connected ? 'Connected' : 'Disconnected'}
      </div>
    </div>
  );
};

export default TemperatureDisplay;
